const theme = cti.store.pages.About.switchTheme;
let newTheme = "";

if (theme) {

  newTheme = "dark";
}

$success(newTheme);

if (cti.store.pages.About.switchLogging) {
  console.log("Enabling support logging...");
  pagerLayer.setSupportLoggingTypes();
}
else {
  console.log("Disabling support logging...");
  pagerLayer.resetSupportLogging();
}

$success();

if (window.cti.store.env.platform.name === "android") { 
    // First go
    window.plugins.insomnia.switchOnScreenAndForeground(function() {$success();}, function() {$fail();}, {});

    // Second go
    setTimeout(function() {window.plugins.insomnia.switchOnScreenAndForeground(function() {}, function() {}, {});}, 1500);
} else if (window.cti.store.env.platform.name === "ios"){
  plugins.insomnia.switchOnScreenAndForeground();
}
$success();

if ($store.env.platform.name === "ios") {
  	if ($store.variables.debug === true) console.log("Disabling Insomnia...");
	window.plugins.insomnia.disable(() => {
    	if ($store.variables.debug === true) console.log("Insomnia successfully disabled.");
      	$success();
    }, (e) => { 
      	console.error("There was an error disabling Insomnia, the app will continue to be kept running in the background:", e);      
        $fail();
    });  
} else {
    // Still need to continue for non-ios platforms
	$success();  
}

// If we don't have any stored credentials then we don't want to enable insomnia
const pagerLayer = window.CtPagerLayer.getInstance("pl_responder");
if ($store.env.platform.name === "ios" && pagerLayer.getCurrentUserJid()) {
  if ($store.variables.debug === true) console.log("Enabling Insomnia...");
  window.plugins.insomnia.enable(() => {
  	// Start a timer to disable Insomnia after the page TTL
    clearTimeout(window.__insomniaTimeout);
    window.__insomniaTimeout = setTimeout(() => {
    	window.cti.utils.callActionflow("disable_insomnia");
    }, CtPagerLayer.getInstance("pl_responder").config.xmpp.maxMessageAlertAge || 3.6e+6);
    if ($store.variables.debug === true) console.log("Insomnia successfully enabled.");
    $success();
  }, (e) => { 
    console.error("There was an error enabling Insomnia:", e);
    $fail(e);
  }); 
} else {
    // Nothing to do so continue as normal
	$success();  
}

if ($store.variables.debug === true) console.log("New Page Received:", $data);
if ($store.env.platform.name === "ios" && $data && $data.$message) {
  	const messageTs = $data.$message.ts;
  	// Keep track of the message timestamps we've seen to avoid needlessly enabling insomnia
  	if ($store.variables.pageTs < messageTs) $store.variables.pageTs = messageTs;
  	if (messageTs >= $store.variables.pushTs) {
  		if ($store.variables.debug === true) console.log("New message arrived with a timestamp >= to the stored value, so disabling Insomnia...");
		$store.variables.pushTs = 0;
    	// Disable insomnia
  		$success(); // This will call the disable_insomnia actionflow within the "Actions" tab of this inline function
    } else {
    	// Dont disable insomnia
		$fail();  
	} 
} else {
    // Dont disable insomnia
	$fail();  
}

// If the message timestamp from the VOIP push is greater than the one we currently have stored
// update the current one with the new one.
if ($data > $store.variables.pushTs) {
  if ($store.variables.debug === true) console.log("Message timestamp from VOIP push greater than stored value so updating to '" + $data + "'.");
  $store.variables.pushTs = $data;
}
$success($data);

