@font-face {
    font-family: 'OpenSans';
    src: url('user-assets/pager_opensans-regular.ttf') format('truetype');
    font-weight: normal;
    font-style: normal;
}

h1, h2, h3, h4, h5, h6, p, span {
    font-family: OpenSans;
}

html, body {
    -webkit-touch-callout: none;
    -webkit-user-select: none;
    user-select: none;
}

html {

    --primary-color: #15233B;
    --primary-font-color: #ffffff;

    --secondary-color: #2c394e;
    --secondary-font-color: #ffffff;

    --header-height: 60px;

    --default-font-color: #333536;
    --default-font-color-light: #9d9d9d;
    --default-placeholder-color: #a8a8a8;

    --default-bg-color: white;
    --default-bg-color-light: white;
    --default-bg-color-dark: #f5f5f5;

    --default-border-color: #d9d9d9;
    --default-select-border: 1px solid var(--default-border-color);

    --button-primary-bg: var(--primary-color);
    --button-primary-text: var(--primary-font-color);
    --button-primary-border: var(--primary-color);

    --button-secondary-bg: transparent;
    --button-secondary-border: none;
    --button-secondary-text: white;

    --button-tertiary-bg: var(--group-primary-color, #64afa8);
    --button-tertiary-border: 1px solid var(--group-primary-color, #64afa8);
    --button-tertiary-text: var(--group-text-color, #fff);

    --button-selected-bg: black;
    --button-selected-border: black;
    --button-selected-text: white;
}

html[theme="dark"] {

    --default-font-color: #fff;
    --default-font-color-light: #9d9d9d;
    --default-placeholder-color: #99AAB5;

    --default-bg-color: #36393e;
    --default-bg-color-dark: #2f3136;
    --default-bg-color-light: #484b52;

    --default-border-color: #484b52;

    --button-secondary-bg: #484b52;
    --button-secondary-border: 1px solid #000;
    --button-secondary-text: #fff;

    --button-cancel-bg: transparent;
    --button-cancel-text: #fff;
    --button-cancel-border: none;
}

html #Login {

    --login-form-font-color: white;
    --login-form-border-color: white;
    --default-bg-color-light: #0067cf;
    --default-border-color: #9b9b9b;

}

.switch-container .switch-text.on {
    color: var(--default-font-color);
}

#title_bar {
    flex: 0 0 var(--header-height);
    display: flex;
    align-items: center;
    justify-content: flex-start;
    padding: 0 20px;
    background: var(--primary-color, #0fb1ca);
    color: var(--primary-font-color, white);
}

#title_bar ct-online-indicator {
    flex: 0 0 auto;
}

#title_bar div#btnNewMessage {
    margin: 0 auto;
    flex: 1 1 50%;
}

#title_bar ct-user-display {
    margin-left: auto;
}

#title_bar div#btnLogout {
    margin: 0 10px 0;
}

#title_bar div.md-button-container a {
    background: var(--primary-color, #0fb1ca);
    color: #fff;
    border: 1px solid #fff;
}


#staticListContainer {
    padding: 0;
    border: 0;
    height: 100%;
}

#staticLogin {
    background-color: #0067cf;
    color: white;
    display: flex;
    flex-direction: column;
    border: none;
}

#staticLogin image-viewer[name="ConsoleLogo"] div {
    display: flex;
    margin: 10vh auto 0;
}

#staticLogin image-viewer[name="ConsoleLogo"] div img {
    margin: auto;
}

#staticLogin #htmlTitle h1,
#staticLogin #htmlTitle h3 {
    text-align: center;
    font-weight: 300;
}

#staticLogin #htmlTitle h1 span {
    font-weight: 500;
}

#staticLogin #htmlTitle h3 {
    font-size: 18px;
}

#staticLogin ct-login-form {
    width: 70vw;
    margin: 40px auto;
    color: white;
}

#staticLogin #htmlTerms {
    margin: auto auto 0;
    text-align: center;
    font-size: 12px;
    width: 70vw;
    opacity: 0.5;
}

.scrollable-item {
    padding: 0;
}

#scrlAbout {
    display: flex;
    flex-direction: column;
    background-color: var(--default-bg-color);
    color: var(--default-font-color);
}

#scrlAbout image-viewer[name="ctLogo"] div {
    display: flex;
    margin: 2vh auto;
}

#scrlAbout image-viewer[name="ctLogo"] div img {
    margin: auto;
    height: 20vh;
}

p.versions { 
    text-align: center;
    color: var(--default-font-color-light);
}

.has-success .control-label {
    color: inherit !important;  
}

#btnEmailLogs a {
    background-color: var(--default-bg-color-light);
    color: var(--default-font-color);
    border: 1px solid var(--default-border-color);
}

@media screen and (max-width: 640px) {

    #title_bar ct-user-display {
        display: none;
    }
}

@media screen and (max-height: 450px) {

    #staticLogin image-viewer,
    #staticLogin #htmlTitle,
    #staticLogin #htmlTerms {
        display: none;
    }

    #staticLogin ct-login-form {
        margin: auto;
    }

}

