// Pager layer destroys data when logging out so reset the pushTs and pageTs to keep in sync
$store.variables.pushTs = 0;
$store.variables.pageTs = 0;
$success();

// If login fails due to invalid credentials then we need to tell the user they need to take action
if (typeof $data === "string" && $data.toLowerCase().includes("invalid username or password") && $store.env.foreground === false) {
  	if ($store.variables.debug === true) console.log("Login failed due to invalid username or password, showing local notification...");
    // Will raise a local notification within the actions tab
	$success();
} else {
    // Won't do anything
	$fail();
}

var stopVibrateAndAudio = false;

if($data.status.status === "disconnected") {
    if($data.status.disconnectStatus.wontReconnect) {
        cti.utils.callAction("go-to-page", { name: "Login" });
      	stopVibrateAndAudio = true;
    }
    if($data.status.disconnectStatus.reason === "Replaced by new connection") {
        cti.utils.callAction("go-to-page", { name: "Login" });
      	stopVibrateAndAudio = true;
    }
    if($data.status.disconnectStatus.reason === "Kicked by administrator") {
        cti.utils.callAction("go-to-page", { name: "Login" });
      	stopVibrateAndAudio = true;
    }
    if($data.status.disconnectStatus.reason === "Conflict") {
        cti.utils.callAction("go-to-page", { name: "Login" });
      	stopVibrateAndAudio = true;
    }
}

$success(stopVibrateAndAudio);

if ($store.env.platform.name === "ios") {
  	setTimeout(() => {
    	window.plugins.insomnia.disable(() => {}, (e) => { 
      		console.error("ðŸ’€ There was an error disabling Insomnia, the app will continue to be kept running in the background:", e);
    	});
    }, $store.preferences.insomniaLimit || 60000);	  
}
if ($store.env.platform.name === "android") {
	window.plugins.insomnia.switchOnScreenAndForeground(() => {}, () => {}, {"showWhenLocked": false, "turnScreenOn": true, "dismissKeyGuard": false, "keepScreenOn": true });
}
$success();

// Get the timestamp from the push, if any.
if ($store.variables.debug === true) console.log("Got a new VOIP push message:", $data.$notification);
if ($data.$notification && $data.$notification.payload && $data.$notification.payload.timestamp) {  	
	let ts = null;
  	try {
      	ts = parseInt($data.$notification.payload.timestamp);
    } catch (e) {
     	console.error("Couldn't parse the message timestamp from the VOIP push message:", e);
    }
  	// Also check to make sure we've not already had a message with this timestamp
  	if (ts !== null && ts > $store.variables.pageTs) {
    	$success(ts);
    }
}
// If there is no timestamp or we have already dealt with newer messages dont do anything, no need to even call subsequent items in this flow

// Only show a local notification if the user is logged in
const pagerLayer = window.CtPagerLayer.getInstance("pl_responder");
if (pagerLayer.getCurrentUserJid()) {
	$success(); // Show the local notification, trigger within the actions tab of this inline function  
} else {
	$fail(); // Don't show the local notification
}
